// Step 1: Define the attack power of each element.
let fireAttackPower = 25; // Fire has an attack power of 25.
let waterAttackPower = 30; // Water has an attack power of 30.
let earthAttackPower = 20; // Earth has an attack power of 20.
let windAttackPower = 15; // Wind has an attack power of 15.

// Step 2: Initialize the monster's health points (HP).
let monsterHP = 100; // The monster starts with 100 HP.

// Step 3: Create an array named 'attackSequence' containing the order of elements to attack with.
let attackSequence = ['fire', 'water', 'earth', 'wind']; // The attack sequence will be: fire, water, earth, then wind.

// Step 4: Print the initial state of the monster's HP.
console.log(`Initial monster HP: ${monsterHP}`);

// Step 5: Iterate over the 'attackSequence' array to attack the monster with each element.
for (let i = 0; i < attackSequence.length; i++) {
    let attackPower = 0; // Initialize attackPower variable
    switch (attackSequence[i]) {
        case 'fire':
            attackPower = fireAttackPower;
            break;
        case 'water':
            attackPower = waterAttackPower;
            break;
        case 'earth':
            attackPower = earthAttackPower;
            break;
        case 'wind':
            attackPower = windAttackPower;
            break;
        default:
            console.log('Unknown element! Skipping attack.');
            continue; // Skip to the next iteration
    }
    monsterHP -= attackPower;
    console.log(`Attacked with ${attackSequence[i]}, monster HP: ${monsterHP}`);
    if (monsterHP <= 0) {
        console.log('The monster is defeated!');
        break; // Exit the loop
    }
}

if (monsterHP > 0) {
    console.log('The monster has survived the attacks.');
} else {
    console.log('Congratulations! You have vanquished the monster with your elemental attacks!');
}
